package packDemoDay2;

public class DeciamalSize {

	static float f;
	static float fMax=Float.MAX_VALUE;
	static float fMin=Float.MIN_VALUE;
	
	static double d;
	static double dMax=Double.MAX_VALUE;
	static double dMin=Double.MIN_VALUE;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("***************Float****************");
		System.out.println("Float default value is  :"+f);
		System.out.println("Float Max Value is  :"+fMax);
		System.out.println("Float Min Value is  :"+fMin);
		System.out.println();
		System.out.println("***************Double****************");
		System.out.println("Double default value is  :"+d);
		System.out.println("Double Max Value is  :"+dMax);
		System.out.println("Double Min Value is  :"+dMin);
	}

}
